INSERT INTO FISH (name, description, size, score) VALUES('Tilapia', 'Peixe de agua doce da familia Cichlidae, conhecido por sua carne saborosa e adaptabilidade a diversos ambientes aquaticos.', '15-30 centimetros', 8);
INSERT INTO FISH (name, description, size, score) VALUES('Salmao', 'O salmao é um peixe de agua doce e salgada, conhecido por sua carne rosada e saborosa. É popular em pratos de frutos do mar e é frequentemente capturado na pesca esportiva.', '30-75 centimetros', 7);
INSERT INTO FISH (name, description, size, score) VALUES('Atum', 'O atum é um peixe de agua salgada encontrado em oceanos de todo o mundo. Ele é valorizado por sua carne rica em proteinas e é amplamente utilizado em sushi e sashimi.', '1-2 metros', 9);
